page_settings.custom_css:
.additional-services { display: none; opacity: 0; transform: translateY(-20px); transition: all 0.4s ease; }
.additional-services.show { display: flex !important; opacity: 1; transform: translateY(0); }
.dropdown-toggle .elementor-button-icon { transition: transform 0.3s ease; }
.dropdown-toggle.active .elementor-button-icon { transform: rotate(180deg); }
.elementor-container:hover { transform: translateY(-5px); transition: transform 0.3s ease; }

page_settings.custom_js:
jQuery(document).ready(function($) {
  $('.dropdown-toggle .elementor-button').on('click', function(e) {
    e.preventDefault();
    var additionalServices = $('.additional-services');
    var button = $(this);
    var buttonText = button.find('.elementor-button-text');

    if (additionalServices.hasClass('show')) {
      additionalServices.removeClass('show');
      setTimeout(function() {
        additionalServices.hide();
      }, 400);
      buttonText.text('View More Services');
      button.removeClass('active');
    } else {
      additionalServices.show();
      setTimeout(function() {
        additionalServices.addClass('show');
      }, 10);
      buttonText.text('Show Less Services');
      button.addClass('active');
    }
  });
});
