// Simple but effective randomization for N8N environment
const generateVariedRandom = () => {
  const r1 = Math.random();
  const r2 = Math.random();
  const r3 = Math.random();

  // Use timestamp for additional entropy (microseconds vary each call)
  const timing = (Date.now() % 10000) / 10000;

  // Combine sources in a way that breaks patterns
  return ((r1 + r2 + r3 + timing) / 4) % 1;
};

const betterShuffle = (array) => {
  if (!array || array.length <= 1) return [...(array || [])];

  const shuffled = [...array];

  // Double Fisher-Yates for better randomization
  for (let pass = 0; pass < 2; pass++) {
    for (let i = shuffled.length - 1; i > 0; i--) {
      const j = Math.floor(generateVariedRandom() * (i + 1));
      [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
    }
  }

  return shuffled;
};

const getVariedCount = (total) => {
  if (!total || total < 3) return Math.min(total, 3); // If less than 3 available, use what we have

  const rand1 = generateVariedRandom();

  // Ensure minimum 3 images, maximum 5
  const minImages = 3;
  const maxImages = Math.min(5, total);

  if (maxImages === minImages) return minImages;

  // Weighted selection for 3-5 images
  const weights = [0.4, 0.35, 0.25]; // for 3, 4, 5 images
  const range = maxImages - minImages + 1;
  const effectiveWeights = weights.slice(0, range);

  let cumulative = 0;
  const totalWeight = effectiveWeights.reduce((sum, w) => sum + w, 0);
  const randomValue = rand1 * totalWeight;

  for (let i = 0; i < effectiveWeights.length; i++) {
    cumulative += effectiveWeights[i];
    if (randomValue <= cumulative) {
      const result = minImages + i;
      console.log(`Selected count: ${result} from ${total} available (min: ${minImages})`);
      return result;
    }
  }

  return minImages; // Fallback to minimum 3
};

// Process and validate images from Google Sheets
try {
  console.log('Processing images from Google Sheets...');
  console.log('Input items:', items.length);

  const images = items
    .map((item, index) => {
      console.log(`Item ${index}:`, JSON.stringify(item.json, null, 2));
      return {
        url: item.json.image_url || item.json.url || item.json.Image || item.json.URL,
        alt: item.json.alt_text || item.json.alt || item.json.Alt || 'Article Image',
        caption: item.json.caption || item.json.Caption || ''
      };
    })
    .filter(img => img.url && img.url.trim() !== '')
    .filter((img, index, arr) => arr.findIndex(i => i.url === img.url) === index);

  console.log(`Processed ${images.length} valid images`);

  if (images.length === 0) {
    console.log('No valid images found, continuing with empty array');
    return [{ 
      json: { 
        images: [],
        total_available: 0,
        selected_count: 0,
        status: 'no_images'
      } 
    }];
  }

  // ENHANCED RANDOMIZATION - Better than original Math.random()
  const shuffled = betterShuffle(images);
  const selectedCount = getVariedCount(images.length);
  const selected = shuffled.slice(0, selectedCount);

  console.log(`Selected ${selected.length} images from ${images.length} available`);

  return [{ 
    json: { 
      images: selected,
      total_available: images.length,
      selected_count: selected.length,
      status: 'success'
    } 
  }];

} catch (error) {
  console.error('Image processing error:', error.message);
  return [{ 
    json: { 
      error: error.message,
      images: [],
      selected_count: 0,
      status: 'error'
    } 
  }];
}

// Convert Markdown to HTML and extract metadata
try {
  console.log('Processing markdown content...');

  const markdownContent = $json.data || $json.body || $json.content || '';

  if (!markdownContent) {
    throw new Error('No markdown content found');
  }

  console.log('Markdown content length:', markdownContent.length);

  let title = 'Untitled Article';
  const titleMatch = markdownContent.match(/^#\s+(.+)$/m);
  if (titleMatch) {
    title = titleMatch[1].trim();
  }

  let excerpt = '';
  const lines = markdownContent.split('\n').filter(line => line.trim());
  for (let line of lines) {
    if (!line.startsWith('#') && line.trim().length > 50) {
      excerpt = line.trim().substring(0, 160) + '...';
      break;
    }
  }

  function parseMarkdown(mdText) {
    const lines = mdText.split('\n');
    let html = '';
    let inCodeBlock = false;
    let listType = null;

    for (let line of lines) {
      line = line.trimEnd();

      if (line.startsWith('```')) {
        if (!inCodeBlock) {
          html += '<pre><code>';
          inCodeBlock = true;
        } else {
          html += '</code></pre>';
          inCodeBlock = false;
        }
        continue;
      }

      if (inCodeBlock) {
        html += line + '\n';
        continue;
      }

      if (listType && !line.match(/^\s*[-*]\s+/) && !line.match(/^\s*\d+\.\s+/)) {
        html += listType === 'ul' ? '</ul>' : '</ol>';
        listType = null;
      }

      if (!line.trim()) {
        if (listType) {
          html += listType === 'ul' ? '</ul>' : '</ol>';
          listType = null;
        }
        continue;
      }

      if (line.match(/^######\s+/)) {
        html += `<h6>${line.replace(/^######\s+/, '')}</h6>`;
      } else if (line.match(/^#####\s+/)) {
        html += `<h5>${line.replace(/^#####\s+/, '')}</h5>`;
      } else if (line.match(/^####\s+/)) {
        html += `<h4>${line.replace(/^####\s+/, '')}</h4>`;
      } else if (line.match(/^###\s+/)) {
        html += `<h3>${line.replace(/^###\s+/, '')}</h3>`;
      } else if (line.match(/^##\s+/)) {
        html += `<h2>${line.replace(/^##\s+/, '')}</h2>`;
      } else if (line.match(/^#\s+/)) {
        html += `<h1>${line.replace(/^#\s+/, '')}</h1>`;
      } else if (line.match(/^\s*[-*]\s+/)) {
        if (listType !== 'ul') {
          html += '<ul>';
          listType = 'ul';
        }
        html += `<li>${line.replace(/^\s*[-*]\s+/, '')}</li>`;
      } else if (line.match(/^\s*\d+\.\s+/)) {
        if (listType !== 'ol') {
          html += '<ol>';
          listType = 'ol';
        }
        html += `<li>${line.replace(/^\s*\d+\.\s+/, '')}</li>`;
      } else {
        if (listType) {
          html += listType === 'ul' ? '</ul>' : '</ol>';
          listType = null;
        }

        let processedLine = line;
        processedLine = processedLine.replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>');
        processedLine = processedLine.replace(/(?<!\*)\*([^*]+)\*(?!\*)/g, '<em>$1</em>');
        processedLine = processedLine.replace(/`([^`]+)`/g, '<code>$1</code>');
        processedLine = processedLine.replace(/\[([^\]]+)\]\(([^)]+)\)/g, '<a href="$2" target="_blank" rel="noopener">$1</a>');
        processedLine = processedLine.replace(/!\[([^\]]*)\]\(([^)]+)\)/g, '<img src="$2" alt="$1" style="max-width: 100%; height: auto;">');

        if (processedLine.trim()) {
          html += `<p>${processedLine}</p>`;
        }
      }
    }

    if (listType) {
      html += listType === 'ul' ? '</ul>' : '</ol>';
    }

    return html;
  }

  const htmlContent = parseMarkdown(markdownContent);

  const paragraphs = [];
  const paragraphMatches = htmlContent.match(/<p[^>]*>(.*?)<\/p>/g);
  if (paragraphMatches) {
    paragraphMatches.forEach(match => {
      const text = match.replace(/<[^>]+>/g, '').trim();
      if (text && text.length > 20) {
        paragraphs.push(text);
      }
    });
  }

  console.log(`Processed article: ${title}`);
  console.log(`Paragraphs extracted: ${paragraphs.length}`);

  return [{
    json: {
      title: title,
      content: htmlContent,
      excerpt: excerpt,
      paragraphs: paragraphs,
      paragraph_count: paragraphs.length,
      word_count: paragraphs.join(' ').split(/\s+/).length,
      status: 'success'
    }
  }];

} catch (error) {
  console.error('Article processing error:', error.message);
  return [{
    json: {
      title: 'Error Processing Article',
      content: `<p>Error: ${error.message}</p>`,
      excerpt: 'Error processing content',
      paragraphs: [],
      paragraph_count: 0,
      word_count: 0,
      status: 'error',
      error: error.message
    }
  }];
}

// Enhanced Combine Data Node - Handles Images, Content, and SEO Data
try {
  const nodeContext = this.getWorkflowStaticData('node');
  const currentData = $json;

  console.log('Combine Data - Current input type:', typeof currentData);
  console.log('Current data keys:', Object.keys(currentData || {}));

  // Detect and store different data types

  // Store images data (from Process Images node)
  if (currentData.images !== undefined || currentData.status === 'no_images' || currentData.status === 'error') {
    nodeContext.imagesData = currentData;
    console.log('‚úÖ Stored images data');
  }

  // Store content data (from Process Content node) 
  // Must have title AND content AND NOT have yoast_meta (to distinguish from SEO data)
  if (currentData.title !== undefined && currentData.content !== undefined && !currentData.yoast_meta && !currentData.seo_data) {
    nodeContext.contentData = currentData;
    console.log('‚úÖ Stored content data');
  }

  // Store SEO data (from Enhanced Yoast SEO Integration)
  // Must have yoast_meta OR seo_data
  if (currentData.yoast_meta !== undefined || currentData.seo_data !== undefined) {
    nodeContext.seoData = currentData;
    console.log('‚úÖ Stored SEO data');
    console.log('SEO data focus keyphrase:', currentData.seo_data?.focus_keyphrase);
  }

  // Debug: Show what we have so far
  console.log('Current storage status:');
  console.log('- Has images:', !!nodeContext.imagesData);
  console.log('- Has content:', !!nodeContext.contentData);
  console.log('- Has SEO:', !!nodeContext.seoData);

  // Check if all THREE data sources are ready
  if (nodeContext.imagesData && nodeContext.contentData && nodeContext.seoData) {
    console.log('üéâ All data sources ready - combining now!');

    const imagesData = nodeContext.imagesData;
    const contentData = nodeContext.contentData;
    const seoData = nodeContext.seoData;

    // Clear stored data to prevent memory issues
    delete nodeContext.imagesData;
    delete nodeContext.contentData;
    delete nodeContext.seoData;

    // Check for content processing errors
    if (contentData.status === 'error') {
      throw new Error(`Content processing error: ${contentData.error}`);
    }

    // Combine all data sources
    const combinedData = {
      // Content data (primary)
      title: contentData.title,
      content: contentData.content,
      excerpt: contentData.excerpt,
      paragraphs: contentData.paragraphs || [],
      paragraph_count: contentData.paragraph_count || 0,
      word_count: contentData.word_count || 0,
      content_status: contentData.status || 'unknown',

      // Images data
      images: imagesData.images || [],
      total_available_images: imagesData.total_available || 0,
      selected_image_count: imagesData.selected_count || 0,
      images_status: imagesData.status || 'unknown',

      // SEO data (from Gemini AI processing)
      yoast_meta: seoData.yoast_meta || {},
      seo_data: seoData.seo_data || {},
      gemini_response: seoData.gemini_response,
      seo_processing_info: seoData.seo_processing_info || {},

      // Processing metadata
      processing_info: {
        combined_at: new Date().toISOString(),
        data_sources_combined: 3,
        seo_integrated: !!(seoData.yoast_meta && Object.keys(seoData.yoast_meta).length > 0),
        focus_keyphrase: seoData.seo_data?.focus_keyphrase || 'N/A',
        total_images: imagesData.images?.length || 0,
        total_paragraphs: contentData.paragraphs?.length || 0
      }
    };

    console.log('‚úÖ Successfully combined all data sources');
    console.log(`üìä Final data includes:`);
    console.log(`   - Title: "${combinedData.title}"`);
    console.log(`   - Content: ${combinedData.content?.length || 0} characters`);
    console.log(`   - Images: ${combinedData.images?.length || 0} selected`);
    console.log(`   - SEO Fields: ${Object.keys(combinedData.yoast_meta || {}).length}`);
    console.log(`   - Focus Keyphrase: "${combinedData.seo_data?.focus_keyphrase || 'N/A'}"`);

    return [{ json: combinedData }];

  } else {
    // Still waiting for more data
    console.log('‚è≥ Still waiting for data...');
    console.log(`Missing: ${!nodeContext.imagesData ? 'images ' : ''}${!nodeContext.contentData ? 'content ' : ''}${!nodeContext.seoData ? 'SEO' : ''}`);
    return []; // Return empty array to wait for more data
  }

} catch (error) {
  console.error('‚ùå Combine data error:', error.message);

  // Clear stored data on error
  const nodeContext = this.getWorkflowStaticData('node');
  delete nodeContext.imagesData;
  delete nodeContext.contentData;
  delete nodeContext.seoData;

  return [{
    json: {
      title: 'Error Combining Data',
      content: `<p>Error: ${error.message}</p>`,
      excerpt: 'Error processing content',
      paragraphs: [],
      images: [],
      yoast_meta: {},
      seo_data: {},
      error: error.message,
      status: 'error',
      processing_info: {
        error: true,
        error_message: error.message,
        processed_at: new Date().toISOString()
      }
    }
  }];
}

// --- Ultra-Fast Smart Content Assembly Node (Fixed for SEO Integration) ---

// Get input data - check if it comes from SEO integration or directly
const inputData = $json;
const html = inputData.content || $json.content;
const imagesRaw = inputData.images || $json.images || [];

// Extract SEO data if available from previous node
const yoastMeta = inputData.yoast_meta || {};
const seoData = inputData.seo_data || {};

/**
 * Ultra-optimized content assembly with preserved dense logic
 */
const assembleContent = (html, images) => {
  if (!html || typeof html !== 'string') return html;
  if (!images || images.length === 0) return html;

  const startTime = Date.now();

  // --- Ultra-fast duplicate filtering (optimized) ---
  const uniqueImages = [];
  const seenUrls = new Set();

  for (const img of images) {
    // Check all possible URL fields and normalize URLs
    const urls = [
      img.url,
      img.medium, 
      img.url_780,
      img.large,
      img.thumbnail
    ].filter(Boolean).map(url => {
      // Fast normalization - just remove query params and fragments
      return url.split('?')[0].split('#')[0];
    });

    // Check if any URL variant has been seen
    const isDuplicate = urls.some(url => seenUrls.has(url));

    if (!isDuplicate && urls.length > 0) {
      uniqueImages.push(img);
      urls.forEach(url => seenUrls.add(url));
    }
  }

  console.log(`Fast filtered ${images.length} images down to ${uniqueImages.length} unique images`);

  // Limit to 2-4 images max
  const maxImages = Math.min(4, Math.max(2, uniqueImages.length));
  const imagePool = uniqueImages
    .sort(() => 0.5 - Math.random())
    .slice(0, maxImages);

  // Helper: get best available image URL - prefer medium sizes
  const getBestImageUrl = (img) => {
    return img.medium || img.url_780 || img.url;
  };

  // --- YOUR CORE DENSE LOGIC (PRESERVED) ---

  // Split content preserving structure
  const parts = html.split(/(<figure[\s\S]*?<\/figure>|<h[1-6][^>]*>[\s\S]*?<\/h[1-6]>)/gi);

  // Check if gap is just whitespace/comments
  const isIgnorableGap = (gap) => /^\s*(?:<!--[\s\S]*?-->\s*|<br\s*\/?>s*)*$/i.test(gap || '');

  // Extract paragraph information
  const findParagraphBlocks = (section) => {
    const re = /<p\b[^>]*>([\s\S]*?)<\/p>/gi;
    const blocks = [];
    let match;

    while ((match = re.exec(section)) !== null) {
      const innerText = match[1].replace(/<[^>]+>/g, '').trim();
      blocks.push({ 
        start: match.index, 
        end: re.lastIndex, 
        inner: match[1].trim(), 
        raw: match[0],
        wordCount: innerText.split(/\s+/).filter(w => w.length > 0).length
      });

      // Prevent infinite loop
      if (re.lastIndex === match.index) re.lastIndex++;
    }
    return blocks;
  };

  // Merge consecutive paragraphs intelligently
  const mergeParagraphRuns = (section) => {
    const blocks = findParagraphBlocks(section);
    if (!blocks || blocks.length <= 1) return section;

    // Group consecutive paragraphs
    const runs = [];
    let runStart = 0;

    for (let i = 0; i < blocks.length; i++) {
      const isLast = i === blocks.length - 1;

      if (!isLast) {
        const gap = section.slice(blocks[i].end, blocks[i + 1].start);
        if (!isIgnorableGap(gap)) {
          runs.push([runStart, i]);
          runStart = i + 1;
        }
      } else {
        runs.push([runStart, i]);
      }
    }

    // Rebuild section with merged paragraphs
    let rebuilt = '';
    let cursor = 0;

    for (const [startIdx, endIdx] of runs) {
      // Add content before first paragraph of run
      const beforeStart = blocks[startIdx].start;
      if (cursor < beforeStart) {
        rebuilt += section.slice(cursor, beforeStart);
      }

      if (endIdx > startIdx) {
        // Merge multiple paragraphs - clean up newlines
        const combinedContent = blocks.slice(startIdx, endIdx + 1)
          .map(block => block.inner)
          .filter(content => content.length > 0)
          .join(' ')
          .replace(/\s+/g, ' ') // Replace multiple spaces/newlines with single space
          .trim();
        rebuilt += `<p>${combinedContent}</p>`;
      } else {
        // Single paragraph - clean up content
        const cleanContent = blocks[startIdx].inner.replace(/\s+/g, ' ').trim();
        rebuilt += `<p>${cleanContent}</p>`;
      }

      cursor = blocks[endIdx].end;
    }

    // Add any remaining content
    if (cursor < section.length) {
      rebuilt += section.slice(cursor);
    }

    return rebuilt;
  };

  // --- OPTIMIZED: Strategic image injection after dense processing ---
  const injectImagesAfterDenseProcessing = (processedContent, availableImages) => {
    if (!availableImages || availableImages.length === 0) return processedContent;

    // Fast separation of headers, existing figures, and content sections
    const sections = [];
    let pos = 0;
    const len = processedContent.length;

    while (pos < len) {
      // Fast search for next structural element
      let nextHeader = processedContent.indexOf('<h', pos);
      let nextFigure = processedContent.indexOf('<figure', pos);

      // Find the earliest structural element
      let nextStructural = -1;
      let structuralType = '';

      if (nextHeader !== -1 && (nextFigure === -1 || nextHeader < nextFigure)) {
        nextStructural = nextHeader;
        structuralType = 'header';
      } else if (nextFigure !== -1) {
        nextStructural = nextFigure;
        structuralType = 'figure';
      }

      if (nextStructural === -1) {
        // No more structural elements, add remaining content
        const remainingContent = processedContent.slice(pos).trim();
        if (remainingContent) {
          sections.push({ type: 'content', content: remainingContent });
        }
        break;
      }

      // Add content before structural element
      if (nextStructural > pos) {
        const content = processedContent.slice(pos, nextStructural).trim();
        if (content) {
          sections.push({ type: 'content', content });
        }
      }

      // Find end of structural element and add it
      let endTag, searchStart;
      if (structuralType === 'header') {
        const headerMatch = processedContent.slice(nextStructural).match(/^<h([1-6])[^>]*>/);
        if (headerMatch) {
          endTag = `</h${headerMatch[1]}>`;
          searchStart = nextStructural + headerMatch[0].length;
        }
      } else {
        endTag = '</figure>';
        searchStart = nextStructural + 7; // '<figure'.length
      }

      if (endTag) {
        const endPos = processedContent.indexOf(endTag, searchStart);
        if (endPos !== -1) {
          const element = processedContent.slice(nextStructural, endPos + endTag.length);
          sections.push({ type: structuralType, content: element });
          pos = endPos + endTag.length;
        } else {
          pos = nextStructural + 1; // Skip this malformed element
        }
      } else {
        pos = nextStructural + 1;
      }
    }

    // Fast paragraph counting across all content sections
    let totalParagraphs = 0;
    for (const section of sections) {
      if (section.type === 'content') {
        let searchPos = 0;
        while ((searchPos = section.content.indexOf('<p', searchPos)) !== -1) {
          totalParagraphs++;
          searchPos += 2;
        }
      }
    }

    if (totalParagraphs === 0) {
      console.log('No paragraphs found after dense processing - skipping image injection');
      return processedContent;
    }

    console.log(`Found ${totalParagraphs} paragraphs after dense processing`);

    // Define injection strategy based on content length and available images
    const maxImagesToUse = Math.min(availableImages.length, Math.floor(totalParagraphs / 2) + 1);
    const injectionPlan = [];

    if (totalParagraphs >= 2) {
      // Always place after 1st paragraph
      injectionPlan.push(1);

      if (totalParagraphs >= 4 && maxImagesToUse > 1) {
        // Place after 2nd-to-last paragraph  
        injectionPlan.push(totalParagraphs - 1);
      }

      // Distribute remaining images in middle sections
      if (maxImagesToUse > 2 && totalParagraphs > 4) {
        const remainingSlots = maxImagesToUse - 2;
        const middleStart = 2;
        const middleEnd = totalParagraphs - 2;

        if (middleEnd > middleStart) {
          const step = Math.floor((middleEnd - middleStart) / remainingSlots);
          for (let i = 0; i < remainingSlots; i++) {
            const position = middleStart + (i * step);
            if (position < middleEnd && !injectionPlan.includes(position)) {
              injectionPlan.push(position);
            }
          }
        }
      }
    } else if (totalParagraphs === 1 && maxImagesToUse > 0) {
      injectionPlan.push(1); // After the only paragraph
    }

    // Sort injection points and ensure uniqueness
    const sortedInjectionPoints = [...new Set(injectionPlan)]
      .sort((a, b) => a - b)
      .slice(0, maxImagesToUse);

    console.log(`Will inject ${sortedInjectionPoints.length} images after paragraphs: [${sortedInjectionPoints.join(', ')}]`);

    // Build final content with images injected ONLY after complete paragraphs
    const finalSections = [];
    let globalParagraphCounter = 0;
    let imageIndex = 0;
    const usedImages = new Set();

    for (const section of sections) {
      finalSections.push(section);

      if (section.type === 'content') {
        // Count paragraphs in this section and inject images after them
        let searchPos = 0;
        while ((searchPos = section.content.indexOf('<p', searchPos)) !== -1) {
          globalParagraphCounter++;

          // Check if this global paragraph position is an injection point
          if (sortedInjectionPoints.includes(globalParagraphCounter) && 
              imageIndex < availableImages.length && 
              imageIndex < maxImagesToUse) {

            const img = availableImages[imageIndex];
            const imageUrl = getBestImageUrl(img);

            if (imageUrl && !usedImages.has(imageUrl)) {
              // Randomly choose between center and left alignment
              const isLeftAligned = Math.random() < 0.5;

              let figureHtml;
              if (isLeftAligned) {
                // Left-aligned figure without text wrap
                figureHtml = `<figure style="display:block;margin:24px 0;text-align:left;clear:both;"><img src="${imageUrl}" alt="${img.alt || 'Article Image'}" style="width:100%;max-width:500px;height:auto;display:block;border-radius:8px;" loading="lazy"/>${img.caption ? `<figcaption style="margin-top:8px;font-style:italic;color:#666;font-size:13px;text-align:left;">${img.caption}</figcaption>` : ''}</figure>`;
              } else {
                // Center-aligned figure (original)
                figureHtml = `<figure style="text-align:center;margin:24px 0;clear:both;"><img src="${imageUrl}" alt="${img.alt || 'Article Image'}" style="width:100%;max-width:600px;height:auto;display:block;margin:0 auto;border-radius:8px;" loading="lazy"/>${img.caption ? `<figcaption style="text-align:center;margin-top:12px;font-style:italic;color:#666;font-size:14px;">${img.caption}</figcaption>` : ''}</figure>`;
              }

              finalSections.push({ type: 'injected-figure', content: figureHtml });
              usedImages.add(imageUrl);
              imageIndex++;

              console.log(`Injected ${isLeftAligned ? 'left-aligned' : 'center-aligned'} image ${imageIndex} after paragraph ${globalParagraphCounter}`);
            }
          }

          searchPos += 2;
        }
      }
    }

    console.log(`Final result: Used ${imageIndex} out of ${availableImages.length} available images`);

    // Reconstruct final HTML from sections
    return finalSections.map(section => section.content).join('');
  };

  // --- EXECUTE YOUR DENSE LOGIC FIRST, THEN OPTIMIZED INJECTION ---
  try {
    console.log('Ultra-fast processing with preserved dense logic...');

    let processedContent = '';

    // STEP 1: Apply your original dense logic (content merging and cleaning)
    const denseStart = Date.now();

    // Combine all text content first, preserving headers and figures
    let allTextContent = '';
    let nonTextParts = [];

    for (let i = 0; i < parts.length; i++) {
      let chunk = parts[i];
      if (!chunk) continue;

      // Keep headers and existing figures separate
      if (/^<h[1-6][^>]*>/i.test(chunk) || /^<figure/i.test(chunk)) {
        // Store position and content of non-text elements
        nonTextParts.push({
          position: allTextContent.length,
          content: chunk
        });
      } else {
        // Apply your dense merging and cleaning logic
        chunk = mergeParagraphRuns(chunk);
        allTextContent += chunk;
      }
    }

    // Reconstruct content with headers/figures in original positions
    processedContent = allTextContent;

    // Insert headers and figures back in their original positions
    for (let i = nonTextParts.length - 1; i >= 0; i--) {
      const part = nonTextParts[i];
      processedContent = processedContent.slice(0, part.position) + part.content + processedContent.slice(part.position);
    }

    console.log(`Dense processing completed in ${Date.now() - denseStart}ms`);

    // STEP 2: Apply optimized image injection AFTER your dense processing
    const injectStart = Date.now();
    if (imagePool.length > 0) {
      console.log(`Applying optimized image injection to densely processed content with ${imagePool.length} images`);
      processedContent = injectImagesAfterDenseProcessing(processedContent, imagePool);
    }
    console.log(`Image injection completed in ${Date.now() - injectStart}ms`);

    // Final cleanup - remove any stray newlines that might have been missed
    const cleanupStart = Date.now();
    processedContent = processedContent
      .replace(/\n+/g, ' ')
      .replace(/\s+/g, ' ')
      .trim();
    console.log(`Final cleanup completed in ${Date.now() - cleanupStart}ms`);

    const totalTime = Date.now() - startTime;
    console.log(`Total processing: ${totalTime}ms for ${html.length} chars (with preserved dense logic)`);

    return processedContent;

  } catch (err) {
    console.error('Enhanced Smart Content Assembly failed:', err.message);
    return html; // Return original HTML on error
  }
};

